import React from 'react';

import './ExpenseFilter.css';
import ExpenseItem from './ExpenseItem';

const ExpensesFilter = ({onHandleSelectYear, selectedYear, setExpenses, expenseData}) => {   

    const dropdownChangeHandler=(e)=>{

        onHandleSelectYear(e.target.value);
        console.log(e.target.value)

        let filteredData = expenseData.filter((item)=>{   
            let getYear = item.date.toString().split(' ').slice(3,4)[0];

            return getYear === e.target.value
        })   


        console.log('filter',filteredData)
        {filteredData.map((expense)=>{
            return (
                <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>
            )
        })}


    }

    return (
        <div className='expenses-filter'>
            <div className='expenses-filter__control'>
                <label>Filter by year</label>
                <select value={selectedYear} onChange={dropdownChangeHandler}>
                    <option value='2023'>2023</option>
                    <option value='2022'>2022</option>
                    <option value='2021'>2021</option>
                    <option value='2020'>2020</option>
                    <option value='2019'>2019</option>
                </select>
            </div>
        </div>
    );
};

export default ExpensesFilter;
