import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import './Expenses.css';

const Expenses=({expenseData})=> {

    var filteredData = [...expenseData];

    const handleSelectYear = (year) => {

        filteredData = expenseData.filter((item)=>{
            console.log(item.date)

            let getYear = item.date.toString().split(' ').slice(3,4)[0];
            return getYear === year
        })   
        console.log(filteredData);    
    }

    return(    
        <Card className="expenses">
            <ExpensesFilter onHandleSelectYear = {handleSelectYear}/>
            <ExpenseItem title={filteredData[0].title} amount={filteredData[0].amount} date={filteredData[0].date}/>
            <ExpenseItem title={filteredData[1].title} amount={filteredData[1].amount} date={filteredData[1].date}/>
            <ExpenseItem title={filteredData[2].title} amount={filteredData[2].amount} date={filteredData[2].date}/>
            <ExpenseItem title={filteredData[3].title} amount={filteredData[3].amount} date={filteredData[3].date}/>
        </Card>

    )
}
export default Expenses;
