import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter";
import Card from "../UI/Card";
import './Expenses.css';

const Expenses=({expenseData, setExpenses})=> {

    const [selectedYear, setSelectedYear] = useState('2020');


    const handleSelectYear = (selectYear) => {
        setSelectedYear(selectYear);

        let filteredData = expenseData.filter((item)=>{   
            let getYear = item.date.toString().split(' ').slice(3,4)[0];

            return getYear === selectYear
        })   
        setExpenses((prevState)=>[...prevState, filteredData]);

    }

    return( 
        <div>
            <Card className="expenses">
                <ExpensesFilter selectedYear={selectedYear} onHandleSelectYear = {handleSelectYear} />
                {expenseData.map((expense)=>{
                    return (
                        <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>
                    )
                })}
            </Card>
        </div>          
    )
}
export default Expenses;
